import * as common from './common';
import * as performance_timing from './performance_timing';
export { common, performance_timing };
export interface HttpSend {
    /** http */
    ev_type: string;
    payload: HttpPayload;
    common: common.Common;
}
export interface HttpPayload {
    /** xhr | fetch | request | jsbridge */
    api: string;
    request: HttpReq;
    response: HttpRes;
    duration: number;
    extra?: {
        [key: string]: string;
    };
    trace?: HttpTrace;
}
export interface HttpReq {
    method: string;
    url: string;
    headers?: {
        [key: string]: string;
    };
    body?: string;
    timestamp: number;
}
export interface HttpRes {
    status: number;
    is_custom_error: boolean;
    headers?: {
        [key: string]: string;
    };
    body?: string;
    timing?: performance_timing.PerformanceResourceTiming;
    timestamp: number;
}
export interface HttpTrace {
    trace_id: string;
    trace_host: string;
    log_id: string;
}
