import * as common from './common';
export { common };
export interface JsErrorSend {
    /** js_error */
    ev_type: string;
    payload: JsErrorPayload;
    common: common.Common;
}
export interface JsErrorPayload {
    error: JsError;
    breadcrumbs: Array<Breadcrumb>;
    extra?: {
        [key: string]: string;
    };
    react?: ReactInfo;
    source?: JsErrorSource;
}
export interface JsErrorSource {
    type: string;
    data?: {
        [key: string]: string;
    };
}
export interface JsError {
    /** 错误名称 */
    name?: string;
    /** 错误信息 */
    message: string;
    /** 堆栈 */
    stack?: string;
    /** 错误文件名 */
    filename?: string;
    lineno?: string;
    colno?: string;
}
export interface Breadcrumb {
    /** dom | http */
    type: string;
    /** xpath, keyvalue | url */
    message: string;
    /** ui.click, ui.keypress | post,get */
    category: string;
    /** status: 400 for http */
    data?: {
        [key: string]: string;
    };
    timestamp: number;
}
export interface ReactInfo {
    version: string;
    componentStack: string;
}
